import React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter, Route, Link } from 'react-router-dom';
import { List } from "./components/List";
import { AddTodo } from "./components/AddTodo";

export const App = () => (
  <BrowserRouter>
    <div>
       <ul>
        <li><Link to='/'>AllTodo</Link></li>
        <li><Link to='/undone'>UndoneTodo</Link></li>
        <li><Link to='/done'>DoneTodo</Link></li>
     </ul>
      <Route exact path='/' component={AllTodoApp} />
      <Route path='/undone' component={UndoneTodo} />
      <Route path='/done' component={DoneTodo} />
    </div>
  </BrowserRouter>
)

export class AllTodoApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      todos:[],
      nextId: 0
    };
  }

  // ToDoを追加
  addTodo = todo => {
    const {nextId} = this.state;
    this.setState({
      todos: [...this.state.todos, {id: nextId, todo: todo, checked: false}],
      nextId: nextId + 1
    })
  };

  //todosを更新する
  change(i) {
    const copiedtodos = this.state.todos.slice();
    copiedtodos[i] = {id: i, todo: copiedtodos[i].todo, checked: !copiedtodos[i].checked} 
        this.setState({todos: copiedtodos})
   }

  // checkedのtrue/falseを切り替える
  changeChecked = id => {
    const {todos} = this.state;
    todos.map(todo => {
      if (todo.id == id) {
         this.change(id);
      };
    });
  };

  render() {
  	return (
  		<div>
        	<h2>TodoApp</h2>
          <AddTodo addTodo={this.addTodo}/>
          <List todos={this.state.todos} changeChecked={this.changeChecked}/>
      </div>
  	);
  }
}

